mapper.update:
update likes set
board_no=#{board_no:INTEGER}, post_no=#{post_no:INTEGER}, cmt_check=#{cmt_check:BOOLEAN}, 
cmt_no=#{cmt_no:INTEGER}, liker_mem_no=#{liker_mem_no:INTEGER}, liking_mem_no=#{liking_mem_no:INTEGER}, 
like_del_check=#{like_del_check:BOOLEAN}
where like_no=#{like_no:INTEGER}
