mapper.update:
update post set
post_type=#{post_type:INTEGER}, board_no=#{board_no:INTEGER}, mem_no=#{mem_no:INTEGER},
post_title=#{post_title:VARCHAR}, post_content= #{post_content:VARCHAR}, post_category=#{post_category:VARCHAR},
post_start_day=#{post_start_day:VARCHAR}, post_end_day=#{post_end_day:VARCHAR}, post_plan=#{post_plan:VARCHAR}
where post_no=#{post_no}
