mapper.insert:
insert into qna
(mem_no, qna_title, qna_content, qna_hits, qna_secret, qna_del_check) 
values (
	#{mem_no:INTEGER}, #{qna_title:VARCHAR}, #{qna_content:VARCHAR},
	#{qna_hits:INTEGER}, #{qna_secret:BOOLEAN}, #{qna_del_check:BOOLEAN}
)

mapper.update:
update qna set 
qna_title=#{qna_title:VARCHAR}, qna_content=#{qna_content:VARCHAR},
qna_hits=#{qna_hits:INTEGER}, qna_secret=#{qna_secret:BOOLEAN}, qna_del_check=#{qna_del_check:BOOLEAN}
where qna_no=#{qna_no}
