mapper.update:
update members set
mem_id=#{mem_id:VARCHAR}, mem_email=#{mem_email:VARCHAR}, mem_password=#{mem_password:VARCHAR}, 
mem_phone=#{mem_phone:VARCHAR},mem_name=#{mem_name:VARCHAR}, mem_sex=#{mem_sex:INTEGER}, mem_birth=#{mem_birth:VARCHAR}, 
mem_address=#{mem_address:VARCHAR}, mem_grant= #{mem_grant:INTEGER}, mem_receive_email=#{mem_receive_email:BOOLEAN}, mem_interest=#{mem_interest:VARCHAR},
mem_token=#{mem_token:VARCHAR}, mem_login_type=#{mem_login_type:VARCHAR}
where mem_no = #{mem_no}
